import React, { useEffect, useState } from "react";
import Layout from "../../components/shared/Layout/Layout";
import { useSelector } from "react-redux";

const AdminHome = () => {
  const { user } = useSelector((state) => state.auth);
  const [typing, setTyping] = useState("");
  const welcomeText = `Welcome Admin ${user?.name}`;

  useEffect(() => {
    if (user?.name) {
      let frontIndex = 0;
      let backIndex = welcomeText.length - 1;
      const intervalId = setInterval(() => {
        setTyping((prev) => 
          welcomeText.substring(0, frontIndex) +
          (frontIndex <= backIndex ? welcomeText[backIndex] : "") +
          prev.substring(frontIndex)
        );
        frontIndex++;
        backIndex--;
        if (frontIndex > backIndex) clearInterval(intervalId);
      }, 150); // Adjust speed as needed
    }
  }, [user?.name]);

  return (
    <Layout>
      <div className="container">
        <div className="d-flex flex-column mt-4">
          <h1 className="typing-animation">
            <i className="text-success">{typing}</i>
          </h1>
          {/* ... rest of your component */}
        </div>
      </div>
    </Layout>
  );
};

export default AdminHome;
