import React from "react";
import Layout from "../../components/shared/Layout/Layout";
import { useSelector } from "react-redux";

const AdminHome = () => {
  const { user } = useSelector((state) => state.auth);
  return (
    <Layout>
      <div className="container">
        <div className="d-felx flex-column mt-4">
          <h1>
            Welcome Admin <i className="text-success">{user?.name}</i>
          </h1>
          <h3>Manage Blood Bank App </h3>
          <hr />
          <p>
          Welcome to the Admin Dashboard of the Blood Bank App. As an admin, you play a crucial role in managing the operations of the blood bank effectively. This dashboard provides you with comprehensive tools to oversee inventory, track donations, manage user roles, generate reports, and perform various administrative tasks. Your dedication and efforts contribute significantly to saving lives and making a positive impact on our community. Thank you for your commitment and valuable contributions to the blood bank's mission."
          </p>
        </div>
      </div>
    </Layout>
  );
};

export default AdminHome;
